import { useCallback, useState } from "react";
import FileInput from "./components/FileInput/FileInput";
import Header from "./components/Header/Header";

function App() {
  const [image, setImage] = useState(null);
  const uploadFinishedHandler = useCallback((imageFile) => {
    setImage(imageFile);
  }, []);

  return (
    <div className="bg-white-20 dark:bg-black-0 h-[100dvh]">
      <Header />
      {image ? (
        <div className="h-[88dvh] sm:h-[92dvh] flex flex-col items-center justify-center">
          <div className="w-[650px] bg-white-0 p-4 rounded-lg">
            <img src={image.url} alt={image.filename} className="w-full rounded-lg"/>
          </div>
        </div>
      ) : (
        <FileInput onImageUploadFinished={uploadFinishedHandler} />
      )}
    </div>
  );
}

export default App;
